CREATE TABLE "public"."preregister_organization"("id" serial NOT NULL, "email" varchar NOT NULL, "password" varchar NOT NULL, "name" varchar NOT NULL, "address" varchar NOT NULL, "phone" varchar NOT NULL, "description" varchar, "invitation_code" varchar, "state" varchar NOT NULL DEFAULT 'pending', "verification_code" varchar NOT NULL, "email_verified" bool NOT NULL DEFAULT false, "created_at" timestamp DEFAULT now(), "updated_at" timestamp DEFAULT now(), PRIMARY KEY ("id") , UNIQUE ("id"), UNIQUE ("email"));
